def get_liquidity_balance(lp_address):
    lp_contract = web3.eth.contract(address=lp_address, abi=lp_abi)
    reserves = lp_contract.functions.getReserves().call()
    balance = reserves[0]
    return balance

def check_liquidity(lp_address):
    balance = get_liquidity_balance(lp_address)
    if balance > 0:
        print(f"{getTimestamp()} Liquidity balance detected'  {balance}")
        #buy()  # We can set up buy as soon as liquidity is added
    else:
        pass
        #print('No liquidity balance detected')



def run_lp_check():
    lp_address = contract.functions.getPair(contract_address, WBNB).call()
    if lp_address != web3.to_checksum_address('0x0000000000000000000000000000000000000000'):
        #print('LP address detected:', lp_address)
        check_liquidity(lp_address)
        return True
    else:
        print('No LP address found')
        return False
print("Checking for Liquidity....")

while True:
    #print("Checking for Liquidity....")
    run_lp_check()
    time.sleep(1)


