    def get_token_details(self, contract_address):
        contract = self.web3.eth.contract(address=contract_address, abi=TOKEN_MODEL_ABI)
        name = contract.functions.name().call()
        decimals = contract.functions.decimals().call()
        DECIMAL = 10 ** decimals
        totalSupply = contract.functions.totalSupply().call() / DECIMAL
        symbol = contract.functions.symbol().call()
        print("==========================================")

        print(style.YELLOW+f"TOKEN INFORMATION ",style.RESET)

        print("==========================================")

        print(f"Contract: {contract_address}\nToken Name: {name}\nToken Symbol: {symbol}\nTotal Supply: {totalSupply}")

    def get_deployer_address(self, contract_address):
        url = f'https://api.etherscan.io/api?module=account&action=txlist&address={contract_address}&startblock=0&endblock=99999999&page=1&offset=3&sort=asc&apikey=QSD4D9KG1NYTX3Y6CPAR62G9FBW16UZ81Z'
        response = requests.get(url)
        data = response.json()

        if data['status'] == '1':
            for tx in data['result']:
                if tx['to'] == contract_address.lower():
                    deployer = self.web3.to_checksum_address(tx['from'])

                    return deployer
        else:
            raise Exception('Error while fetching transactions: ' + data['message'])

