    def getlpAddress(self, contract_address):
        # Check for V2 LP address
        lp_address_v2 = self.uniswap_v2_factory.functions.getPair(contract_address, WETH_ADDRESS).call()
        if lp_address_v2 != '0x0000000000000000000000000000000000000000':
            #print(f"Uniswap V2 LP Address: {lp_address_v2}")
            return  lp_address_v2

        # If we reach here, it means no V2 LP was found. Now check for V3 LP address.
        fee_tiers = [100,500, 3000, 10000]  # These are the fee tiers in basis points: 0.05%, 0.3%, 1%
        for fee in fee_tiers:
            lp_address_v3 = self.uniswap_v3_factory.functions.getPool(WETH_ADDRESS, contract_address, fee).call()
            if lp_address_v3 != '0x0000000000000000000000000000000000000000':
                #print(f"Uniswap V3 LP Address: {lp_address_v3}, Fee: {fee}")
                return lp_address_v3
        else:
            print("No Uniswap V2 or V3 LP found.")
